import React, { Component } from 'react';
import './App.css';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import 'bootstrap/dist/css/bootstrap.css';

class MasterForm extends React.Component {

  loading_state = {
    loading: true
  };

  componentDidMount() {
    // this simulates an async action, after which the component will render the content
    this.setState({ loading: false }),  
    console.log(loading_state);
  }


  constructor(props) {
    super(props)
    this.state = {
      currentStep: 1,
      email:  '',
      username: '',
      password: '', 
    }
  }

  handleChange = event => {
    const {name, value} = event.target
    this.setState({
      [name]: value
    })    
  }

  handleSubmit = event => {
    event.preventDefault()
    const { name, username, password } = this.state
    alert(`Your registration detail: \n 
           Name: ${name} \n 
           Username: ${username} \n
           Password: ${password}`)
  }

  _next = () => {
    let currentStep = this.state.currentStep
    currentStep = currentStep >= 2? 3: currentStep + 1
    this.setState({
      currentStep: currentStep
    })
  }

  _prev = () => {
    let currentStep = this.state.currentStep
    currentStep = currentStep <= 1? 1: currentStep - 1
    this.setState({
      currentStep: currentStep
    })
  }

/*
* the functions for our button
*/
previousButton() {
  let currentStep = this.state.currentStep;
  if(currentStep !==1){
    return (
      <Button variant="secondary float-left"
       onClick={this._prev} >
      Previous
      </Button>
    )
  }
  return null;
}

nextButton(){
  let currentStep = this.state.currentStep;
  if(currentStep <3){
    return (
      <Button variant="primary float-right"
       onClick={this._next} >
      Next
      </Button>        
    )
  }
  return null;
}



render() {
  const {loading} = this.loading_state;
  if(loading) { // if your component doesn't have to wait for an async action, remove this block 
  return null; // render null when app is not ready
}
  return (
      <React.Fragment>

      <Container>
        <div>
          <h1 class ='title'>Food Details</h1>
        </div>
      <div className = "content">
      <Form onSubmit={this.handleSubmit}>
      {/* 
        render the form steps and pass required props in
      */}
        <Step1 
          currentStep={this.state.currentStep} 
          handleChange={this.handleChange}
          name={this.state.name}
        />
        <Step2 
          currentStep={this.state.currentStep} 
          handleChange={this.handleChange}
          username={this.state.username}
        />
        <Step3 
          currentStep={this.state.currentStep} 
          handleChange={this.handleChange}
          password={this.state.password}
        />
         {this.nextButton()}
        {this.previousButton()}


      </Form>

      </div>

      </Container>

      </React.Fragment>
    );
  }
}

function Step1(props) {
  if (props.currentStep !== 1) {
    return null
  } 
  return(

    <div className="form-group">
        <label htmlFor="name">Name</label>
        <input
          className="form-control"
          id="name"
          name="name"
          type="text"
          placeholder="Enter Name"
          value={props.name}
          onChange={props.handleChange}
          />
   </div>

  );
}

function Step2(props) {
  if (props.currentStep !== 2) {
    return null
  } 
  return(


    <div className="form-group">
      <label htmlFor="username">Username</label>
      <input
        className="form-control"
        id="username"
        name="username"
        type="text"
        placeholder="Enter username"
        value={props.username}
        onChange={props.handleChange}
        />
    </div>

  );
}


function demoAsyncCall() {
  return new Promise((resolve) => setTimeout(() => resolve(), 100));
}



function Step3(props) {
  if (props.currentStep !== 3) {
    return null
  } 
  return(
    <React.Fragment>
    <div className="form-group">
      <label htmlFor="password">Password</label>
      <input
        className="form-control"
        id="password"
        name="password"
        type="password"
        placeholder="Enter password"
        value={props.password}
        onChange={props.handleChange}
        />      
    </div>
    <Button variant="success float-right">Sign up</Button>
    </React.Fragment>
  );
};



export default MasterForm;

