import React, { Component } from 'react';
import './App.css';
import Form from 'react-bootstrap/Form';
import Col from 'react-bootstrap/Col';
import Container from 'react-bootstrap/Container';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal';
import 'bootstrap/dist/css/bootstrap.css';
import ToggleButtonGroup from 'react-bootstrap/ToggleButtonGroup';
import ToggleButton from 'react-bootstrap/ToggleButton';
import { ControlLabel } from 'react-bootstrap';
import Select from 'react-select';
import makeAnimated from 'react-select/animated';
import { useState } from 'react';


class MasterForm extends React.Component {

  componentDidMount() {
    this.props.hideLoader();
  }

  constructor(props) {
    super(props);
    this.state = {
      isLoading: false,
      currentStep: 1,
      formData:{
      name:  '',
      calories: '',
      protein: '', 
      fats: '', 
      carbs: '',
      allergens : []

      },
      result: ""
    };
  }


  handlelist = (e) => {
    this.state.formData.allergens = []

    var a;
    for(a in e) {
      this.state.formData.allergens.push(e[a]['value']);
    }

  }

  handleChange = (event) => {
    const value = event.target.value;
    const name = event.target.name;
    let target = event.target;
    var formData = this.state.formData;
    formData[name] = value;
    this.setState({
      formData
    });
    console.log(formData)
  }

handleResponse = (r) =>{
  console.log(r);
  this.setState({
    result: r.result,
    isLoading: false
  });
  Example()
}

handlePredictClick = () => {
    const formData = this.state.formData;
    console.log(formData);
    this.setState({isLoading: true });
    fetch('http://127.0.0.1:5000/prediction/', 
      {
        headers: {
          'Accept': 'application/json',
          'Content-Type': 'application/json'
        },
        method: 'POST',
        body: JSON.stringify(formData)
      })
      .then(response => response.json())
      .then(response => this.handleResponse(response));
      // .then(response => {
      //   this.setState({
      //     result: response.result,
      //     isLoading: false
      //   });
      // })


  }



  _next = () => {
    let currentStep = this.state.currentStep
    currentStep = currentStep >= 3? 4: currentStep + 1
    this.setState({
      currentStep: currentStep
    })
  }

  _prev = () => {
    let currentStep = this.state.currentStep
    currentStep = currentStep <= 1? 1: currentStep - 1
    this.setState({
      currentStep: currentStep
    })
  }

/*
* the functions for our button
*/
previousButton() {
  let currentStep = this.state.currentStep;
  if(currentStep !==1){
    return (
      <Button variant="secondary float-left"
       onClick={this._prev} >
      Previous
      </Button>
    )
  } 
  return null;
}

nextButton(){
  let currentStep = this.state.currentStep;
  if(currentStep < 4){
    return (

      <Button variant="primary float-right"
       onClick={this._next} >
      Next
      </Button>  

    )
  } else if(currentStep = 4){
   return (
    <Button variant="primary float-right"
    onClick={this.handlePredictClick} >
   Submit
   </Button> )
   };


}

render() {

  return (
      <React.Fragment>

      <Container>
        <div>
          <h1 className ='title'>Food Details</h1>
        </div>
      <div className = "content">

      <Form onSubmit={this.handleSubmit}>

      {/* 
        render the form steps and pass required props in
      */}
        <Step1 
          currentStep={this.state.currentStep} 
          handleChange={this.handleChange}
          email={this.state.formData.email}
        />
        <Step2 
          formData = {this.state.formData}
          currentStep={this.state.currentStep} 
          handleChange={this.handleChange}

        />
        <Step3 
          currentStep={this.state.currentStep} 
          handleChange={this.handleChange}
          calories={this.state.formData.calories}
          protein={this.state.formData.protein}
          fats={this.state.formData.fats}
          carbs={this.state.formData.carbs}
        />

        <Step4 
          currentStep={this.state.currentStep} 
          handleChange={this.handlelist}
          allergens={this.state.formData.allergens}
        />        

         {this.nextButton()}
        {this.previousButton()}
      </Form>

      {this.state.result === "" ? null :
            (<Row>
              <Col className="result-container">
                <h5 id="result">{this.state.result}</h5>
              </Col>
            </Row>)
          }


      </div>

      </Container>

      </React.Fragment>
    );
  }
}

function Step1(props) {
  if (props.currentStep !== 1) {
    return null
  } 

  return(

    <div className="form-group">
        <label htmlFor="email">Name</label>
        {/* <input
          className="form-control"
          id="email"
          name="email"
          type="text"
          placeholder="Enter Name"
          value={props.name}
          onChange={props.handleChange}

          /> */}
            <Form.Control 
                  type="text" 
                  placeholder="Enter email" 
                  name="email"
                  value={props.email}
                  onChange={props.handleChange} />
   </div>

  );
}


function Step2(args) {
  const formData = args.formData;
  if (args.currentStep !== 2) {
    return null
  }

  return(
    <div className="form-group">
     Hi {args.formData.email} lets get you less fat
    </div>

  );

}


function Step3(props) {
  if (props.currentStep !== 3) {
    return null
  } 
  return(
    <React.Fragment>
    {/* <div className="form-group">
      <label htmlFor="password">Password</label>
      <input
        className="form-control"
        id="password"
        name="password"
        type="password"
        placeholder="Enter password"
        value={props.password}
        onChange={props.handleChange}
        />      
    </div> */}

    <Form>
      <Form.Row>

          <Form.Group as={Col}  controlId="formGridAddress1">
            <Form.Label>Calories</Form.Label>
            <Form.Control 
                          type="float" 
                          placeholder="Enter Calories" 
                          name="calories"
                          value={props.calories}
                          onChange={props.handleChange} />
          </Form.Group>

          <Form.Group as={Col}  controlId="formGridAddress2">
              <Form.Label>Protein</Form.Label>
              <Form.Control 
                            type="float" 
                            placeholder="Enter Protein" 
                            name="protein"
                            value={props.protein}
                            onChange={props.handleChange} />
          </Form.Group>
      </Form.Row>

      <Form.Row>
          <Form.Group as={Col}  controlId="formGridAddress4">
              <Form.Label>Fats</Form.Label>
              <Form.Control 
                            type="float" 
                            placeholder="Enter Fats" 
                            name="fats"
                            value={props.fats}
                            onChange={props.handleChange} />
          </Form.Group>

          <Form.Group as={Col}  controlId="formGridAddress4">
              <Form.Label>Carbs</Form.Label>
              <Form.Control 
                            type="float" 
                            placeholder="Enter Carbs" 
                            name="carbs"
                            value={props.carbs}
                            onChange={props.handleChange} />
